renderHTML:
__ht__function(data, gv, cache) {

    var option = {
        color: ['rgb(242,83,75)', 'rgb(242,83,75)'],
        textStyle: {
            color: 'rgb(222,222,222)',
        },
        tooltip: {
            trigger: 'axis',
            axisPointer: {
                type: 'shadow'
            }
        },
        legend: {
            show: true,
            left: 'right',
            right: 0,
            top: '2%',
            textStyle: {
            color: 'rgb(222,222,222)',
            }
        },
        grid: {
            left: '3%',
            right: '3%',
            bottom: '3%',
            top: '20%',
            containLabel: true
        },
        xAxis: [{
            data: [],
            axisLine: {show: true},
            splitLine: {show: false},
            axisLabel: {

            }
        }],
        yAxis: [
            {
                type: 'value',
                name: '值',
                min: 260,
                max: 320,
                axisLine: {show: true},
                splitLine: {show: false},
                axisLabel: {}
            }
        ],
        series: [
            {
                name: '实际值',
                type: 'line',
                itemStyle: {
                     color: new echarts.graphic.LinearGradient(
                            0, 0, 0, 1,
                            [
                                {offset: 0, color: 'rgb(38,130,222)'},
                                {offset: 1, color: 'rgb(50,181,217)'}
                            ]
                       )
                },
                data: []
            }
        ]
    }

    if (!cache.htmlView) {
        cache.htmlView = document.createElement('div');
        cache.htmlView.style.position = 'absolute';
        cache.htmlChart = echarts.init(cache.htmlView);

        // layoutHTML will be called when the data needs to be drawing
        cache.htmlView.layoutHTML = function() {
            gv.layoutHTML(data, cache.htmlView, false);
            cache.htmlChart.resize();
        };
    }

    // update data
    option.series[0].data = data.a('dataset');
    option.xAxis[0].data = data.a('xAxisData');
    cache.htmlChart.setOption(option);

    // html for showing
    return cache.htmlView;
}
