renderHTML:
__ht__function(data, gv, cache) {
    var option = {
        color: ['rgb(51,153,255)'],
        tooltip : {
            trigger: 'axis',
            axisPointer: {
                type: 'line',
                label: {
                    backgroundColor: '#6a7985'
                }
            }
        },
        legend: {
            show: true,
            left: '80%',
            right: 0,
            top: '30%',
            orient: 'vertical'
        },
        grid: {
            left: '3%',
            right: 32,
            bottom: 0,
            top: '5%',
            containLabel: true
        },
        xAxis : [
            {
                type : 'category',
                boundaryGap : false,
                axisLine: {show: false},
                splitLine: {show: false},
                axisLabel: {
                    color: 'rgb(124,145,155)'
                }
            }
        ],
        yAxis : [
            {
                type : 'value',
                axisLine: {show: false},
                splitLine: {show: false},
                max: 340,
                min: 240,
                axisLabel: {
                    color: 'rgb(124,145,155)'
                }
            }
        ],
        series : [
            {
                type:'line',
                smooth: true,
                areaStyle: {
                    normal: {
                        color: new echarts.graphic.LinearGradient(0, 0, 0, 1, [{
                            offset: 0,
                            color: 'rgb(51,153,255)'
                        }, {
                            offset: 1,
                            color: 'rgba(51,153,255, 0.1)'
                        }])
                    }
                },
                markLine: {
                    data: [{
                        yAxis: 260
                    }, {
                        yAxis: 320
                    }],
                    lineStyle: {
                        color: 'rgb(242,83,75)'
                    }
                }
            }
        ]
    };

    if (!cache.htmlView) {
        cache.htmlView = document.createElement('div');
        cache.htmlView.style.position = 'absolute';
        cache.htmlChart = echarts.init(cache.htmlView);

        // layoutHTML will be called when the data needs to be drawing
        cache.htmlView.layoutHTML = function() {
            gv.layoutHTML(data, cache.htmlView, false);
            cache.htmlChart.resize();
        };
    }

    // update data
    option.series[0].data = data.a('dataset');
    option.xAxis[0].data = data.a('xAxisData');
    cache.htmlChart.setOption(option);
    // html for showing
    return cache.htmlView;
}
